() => {
  const iframe = document.querySelector('#storybook-preview-iframe');
  if (iframe) {
    const rect = iframe.getBoundingClientRect();
    return { top: rect.top, left: rect.left, width: rect.width, height: rect.height };
  }
  return null;
}

