  font-weight: 100 900;
  font-style: normal italic;
  font-display: swap;
}

@theme {
  --font-sans: "Helvetica Neue", "Helvetica", "Inter", -apple-system, sans-serif;
  --font-serif: "Newsreader", "Times", serif;
  --font-mono: "Newsreader", serif;

  --radius-lg: var(--radius);
  --radius-md: calc(var(--radius) - 2px);
  --radius-sm: calc(var(--radius) - 4px);

  --breakpoint-xs: 476px;
  --breakpoint-toast-mobile: 600px;

  --color-background: oklch(var(--background));
