@layer base {
  *,
  ::after,
  ::before,
  ::backdrop,
  ::file-selector-button {
    border-color: var(--color-border, currentColor);
  }

  * {
    @apply outline-ring/50;
  }

  html {
    scrollbar-gutter: stable;
    scrollbar-width: thin;
  }

  body {
    background-color: var(--color-background);
    color: var(--color-foreground);
    font-family: var(--font-sans);
    font-size: 16px;
    line-height: 28px;
    text-rendering: optimizeLegibility;
    -webkit-font-smoothing: antialiased;
    -moz-osx-font-smoothing: grayscale;
  }

  h2 {
    font-family: var(--font-serif);
    font-style: italic;
  }

  em,
  i {
    font-family: var(--font-serif);
    font-style: italic;
    font-size: calc(1rem + 1px);
  }

  .dark body {
    color-scheme: dark;
  }

  .dark .mask canvas {
    filter: invert(1);
  }

  div {
    @apply relative;
  }

  textarea {
    field-sizing: content;
    resize: none;
  }

  .error {
    color: rgb(255, 0, 0);
    font-size: smaller;
  }

  li {
    list-style: none;
  }

  input[type="number"]::-webkit-outer-spin-button,
  input[type="number"]::-webkit-inner-spin-button {
    -webkit-appearance: none;
    margin: 0;
  }

  input[type="number"] {
    -moz-appearance: textfield;
    appearance: textfield;
  }
}

.mask {
  mask-image: radial-gradient(rgba(0, 0, 0, 1) 0%, transparent 80%);
}

.prose-blog {
  --tw-prose-body: oklch(var(--foreground) / 0.75);
  --tw-prose-headings: var(--color-foreground);
  --tw-prose-lead: var(--color-muted-foreground);
  --tw-prose-links: var(--color-foreground);
  --tw-prose-bold: var(--color-foreground);
  --tw-prose-counters: var(--color-muted-foreground);
  --tw-prose-bullets: var(--color-bullet);
  --tw-prose-hr: var(--color-border);
  --tw-prose-quotes: var(--color-foreground);
  --tw-prose-quote-borders: var(--color-border-strong);
  --tw-prose-captions: var(--color-muted-foreground);
  --tw-prose-code: var(--color-foreground);
  --tw-prose-pre-code: #ededed;
  --tw-prose-pre-bg: #1a1a1a;
  --tw-prose-th-borders: var(--color-border-strong);
  --tw-prose-td-borders: var(--color-border);

  font-size: 16px;
  line-height: 28px;
  text-rendering: optimizeLegibility;
  -webkit-font-smoothing: antialiased;
  -moz-osx-font-smoothing: grayscale;

  & h2 {
    font-family: var(--font-serif);
    font-style: italic;
    font-weight: 400;
    font-size: calc(1rem + 1px);
    margin-top: 2rem;
    margin-bottom: 2rem;
  }

  & em,
  & i {
    font-family: var(--font-serif);
    font-style: italic;
    font-size: calc(1rem + 1px);
    font-weight: 500;
  }

  & a {
    color: inherit;
    text-decoration: none;
  }

  & a:hover {
    text-decoration: underline;
  }
}

@keyframes gridMovingLine {
  0% {
    transform: translateX(100%);
    opacity: 0;
  }
  20% {
    opacity: 100;
  }
  100% {
    transform: translateX(-100%);
    opacity: 100;
  }
}

@layer utilities {
  .text-balance {
    text-wrap: balance;
  }

  .no-scrollbar {
    -ms-overflow-style: none;
    scrollbar-width: none;
  }

  .no-scrollbar::-webkit-scrollbar {
    display: none;
  }

  .text-label {
    letter-spacing: 0.1em;
    text-transform: uppercase;
    color: var(--color-subtle);
  }

  .corners-border {
    position: relative;
  }

  .corners-border::before {
    content: "";
    position: absolute;
    inset: 0;
    pointer-events: none;
    transition: opacity 150ms ease;
    opacity: 0.2;
    background:
      linear-gradient(currentColor, currentColor) top left / 6px 1px no-repeat,
      linear-gradient(currentColor, currentColor) top left / 1px 6px no-repeat,
      linear-gradient(currentColor, currentColor) top right / 6px 1px no-repeat,
      linear-gradient(currentColor, currentColor) top right / 1px 6px no-repeat,
      linear-gradient(currentColor, currentColor) bottom left / 6px 1px no-repeat,
      linear-gradient(currentColor, currentColor) bottom left / 1px 6px no-repeat,
      linear-gradient(currentColor, currentColor) bottom right / 6px 1px no-repeat,
      linear-gradient(currentColor, currentColor) bottom right / 1px 6px no-repeat;
  }

  .corners-border:hover::before {
    opacity: 0.45;
  }
}

