import { Button as ButtonPrimitive } from "@base-ui/react/button";
import { cn } from "@laxdb/ui/lib/utils";
import { cva, type VariantProps } from "class-variance-authority";

const buttonVariants = cva(
  "cursor-pointer focus-visible:border-ring focus-visible:ring-ring/30 aria-invalid:ring-destructive/20 dark:aria-invalid:ring-destructive/40 aria-invalid:border-destructive dark:aria-invalid:border-destructive/50 rounded-none border border-transparent bg-clip-padding text-xs/relaxed font-medium focus-visible:ring-[2px] aria-invalid:ring-[2px] [&_svg:not([class*='size-'])]:size-4 inline-flex items-center justify-center whitespace-nowrap transition-all duration-150 disabled:pointer-events-none disabled:opacity-50 [&_svg]:pointer-events-none shrink-0 [&_svg]:shrink-0 outline-none group/button select-none",
  {
    variants: {
      variant: {
        default:
          "bg-primary text-primary-foreground border-b border-b-black/5 dark:border-b-white/5 shadow-[0_0_0_1px_rgba(0,0,0,0.03),0_1px_3px_0_rgba(0,0,0,0.12),inset_0_1px_0_0_rgba(255,255,255,0.06)] hover:shadow-[0_0_0_1px_rgba(0,0,0,0.03),0_3px_8px_-2px_rgba(0,0,0,0.2),inset_0_1px_0_0_rgba(255,255,255,0.1)] active:shadow-[inset_0_2px_4px_0_rgba(0,0,0,0.2)] active:translate-y-px active:border-b-transparent",
        outline:
          "border-border bg-background shadow-[0_1px_2px_0_rgba(0,0,0,0.04)] dark:bg-input/30 hover:bg-accent/50 hover:border-border-strong hover:text-foreground hover:shadow-[0_2px_4px_-1px_rgba(0,0,0,0.08)] active:shadow-none active:translate-y-px aria-expanded:bg-muted aria-expanded:text-foreground",
        secondary:
          "bg-secondary text-secondary-foreground border-border/50 shadow-[0_1px_2px_0_rgba(0,0,0,0.04)] hover:bg-secondary/80 hover:border-border hover:shadow-[0_2px_4px_-1px_rgba(0,0,0,0.08)] active:shadow-none active:translate-y-px aria-expanded:bg-secondary aria-expanded:text-secondary-foreground",
        ghost:
          "hover:bg-accent hover:text-accent-foreground hover:shadow-[0_1px_2px_0_rgba(0,0,0,0.03)] dark:hover:bg-accent/60 active:scale-[0.97] aria-expanded:bg-muted aria-expanded:text-foreground",
        destructive:
          "bg-destructive/10 text-destructive shadow-[0_1px_2px_0_rgba(0,0,0,0.04)] hover:bg-destructive/20 hover:shadow-[0_2px_4px_-1px_rgba(0,0,0,0.08)] active:shadow-none active:translate-y-px focus-visible:ring-destructive/20 dark:focus-visible:ring-destructive/40 dark:bg-destructive/20 focus-visible:border-destructive/40 dark:hover:bg-destructive/30",
        bracket:
          "corners-border text-foreground hover:text-foreground/70",
        link: "text-primary underline-offset-4 hover:underline",
      },
      size: {
        default:
          "h-7 gap-1 px-2 text-xs/relaxed has-data-[icon=inline-end]:pr-1.5 has-data-[icon=inline-start]:pl-1.5 [&_svg:not([class*='size-'])]:size-3.5",
        xs: "h-5 gap-1 px-2 text-[0.625rem] has-data-[icon=inline-end]:pr-1.5 has-data-[icon=inline-start]:pl-1.5 [&_svg:not([class*='size-'])]:size-2.5",
        sm: "h-6 gap-1 px-2 text-xs/relaxed has-data-[icon=inline-end]:pr-1.5 has-data-[icon=inline-start]:pl-1.5 [&_svg:not([class*='size-'])]:size-3",
        lg: "h-8 gap-1 px-2.5 text-xs/relaxed has-data-[icon=inline-end]:pr-2 has-data-[icon=inline-start]:pl-2 [&_svg:not([class*='size-'])]:size-4",
        icon: "size-7 [&_svg:not([class*='size-'])]:size-3.5",
        "icon-xs": "size-5 [&_svg:not([class*='size-'])]:size-2.5",
        "icon-sm": "size-6 [&_svg:not([class*='size-'])]:size-3",
        "icon-lg": "size-8 [&_svg:not([class*='size-'])]:size-4",
      },
    },
    defaultVariants: {
      variant: "default",
      size: "default",
    },
  },
);

function Button({
  className,
  variant = "default",
  size = "default",
  ...props
}: ButtonPrimitive.Props & VariantProps<typeof buttonVariants>) {
  return (
    <ButtonPrimitive
      data-slot="button"
      className={cn(buttonVariants({ variant, size, className }))}
      {...props}
    />
  );
}

export { Button, buttonVariants };

