    letter-spacing: 0.1em;
    text-transform: uppercase;
    color: var(--color-subtle);
  }

  .corners-border {
    position: relative;
  }

  .corners-border::before {
    content: "";
    position: absolute;
    inset: -1px;
    pointer-events: none;
    transition: opacity 150ms ease;
    opacity: 0.1;
    background:
      linear-gradient(currentColor, currentColor) top left / 5px 1px no-repeat,
      linear-gradient(currentColor, currentColor) top left / 1px 5px no-repeat,
      linear-gradient(currentColor, currentColor) top right / 5px 1px no-repeat,
