      linear-gradient(currentColor, currentColor) bottom right / 5px 1px no-repeat,
      linear-gradient(currentColor, currentColor) bottom right / 1px 5px no-repeat;
  }

  .corners-border:hover::before,
  .corners-border-fixed::before {
    opacity: 0.3;
  }
}

