  1→import { FileSystem, Path } from "@effect/platform";
  2→import { BunContext } from "@effect/platform-bun";
  3→import { Duration, Effect, Either, Layer, Schema } from "effect";
  4→
  5→import { PLLClient } from "../../pll/pll.client";
  6→import {
  7→  PLLEvent,
  8→  PLLPlayer,
  9→  PLLTeam,
 10→  type PLLAdvancedPlayer,
 11→  type PLLEventDetail,
 12→  type PLLGraphQLStanding,
 13→  type PLLPlayerDetail,
 14→  type PLLTeamDetail,
 15→  type PLLTeamStanding,
 16→} from "../../pll/pll.schema";
 17→import { ExtractConfigService } from "../extract.config";
 18→import {
 19→  type SeasonManifest,
 20→  emptyExtractResult,
 21→  withTiming,
 22→} from "../extract.schema";
 23→import {
 24→  IncrementalExtractionService,
 25→  type IncrementalExtractOptions,
 26→} from "../incremental.service";
 27→import { isCriticalError, saveJson, withRateLimitRetry } from "../util";
 28→
 29→import { PLLManifestService } from "./pll.manifest";
 30→
 31→const PLL_YEARS = [2019, 2020, 2021, 2022, 2023, 2024, 2025] as const;
 32→
 33→export class PLLExtractorService extends Effect.Service<PLLExtractorService>()(
 34→  "PLLExtractorService",
 35→  {
 36→    effect: Effect.gen(function* () {
 37→      const pll = yield* PLLClient;
 38→      const config = yield* ExtractConfigService;
 39→      const manifestService = yield* PLLManifestService;
 40→      const incremental = yield* IncrementalExtractionService;
 41→      const fs = yield* FileSystem.FileSystem;
 42→      const path = yield* Path.Path;
 43→      yield* Effect.log(`Output directory: ${config.outputDir}`);
 44→
 45→      const getOutputPath = (year: number, entity: string) =>
 46→        path.join(config.outputDir, "pll", String(year), `${entity}.json`);
 47→
 48→      const readJsonFile = (filePath: string) =>
 49→        fs
 50→          .readFileString(filePath, "utf-8")
 51→          .pipe(
 52→            Effect.catchTag("SystemError", (error) =>
 53→              Effect.zipRight(
 54→                Effect.logWarning(
 55→                  `Could not read file at ${filePath}, using empty array`,
 56→                  error,
 57→                ),
 58→                Effect.succeed("[]"),
 59→              ),
 60→            ),
 61→          );
 62→
 63→      /** Extracts teams for a season. @see isCriticalError for error handling. */
 64→      const extractTeams = (year: number) =>
 65→        Effect.gen(function* () {
 66→          yield* Effect.log(`  📊 Extracting teams for ${year}...`);
 67→          const result = yield* pll
 68→            .getTeams({ year, includeChampSeries: true })
 69→            .pipe(withTiming(), withRateLimitRetry(), Effect.either);
 70→          if (Either.isLeft(result)) {
 71→            yield* Effect.log(
 72→              `     ✗ Failed [${result.left._tag}]: ${result.left.message}`,
 73→            );
 74→            if (isCriticalError(result.left)) {
 75→              return yield* Effect.fail(result.left);
 76→            }
 77→            return emptyExtractResult([] as readonly PLLTeam[]);
 78→          }
 79→          yield* saveJson(getOutputPath(year, "teams"), result.right.data);
 80→          yield* Effect.log(
 81→            `     ✓ ${result.right.count} teams (${result.right.durationMs}ms)`,
 82→          );
 83→          return result.right;
 84→        });
 85→
 86→      const extractTeamDetails = (year: number, teams: readonly PLLTeam[]) =>
 87→        Effect.gen(function* () {
 88→          yield* Effect.log(`  🏟️ Extracting team details for ${year}...`);
 89→          const start = Date.now();
 90→
 91→          const results = yield* Effect.forEach(
 92→            teams,
 93→            (team) =>
 94→              pll
 95→                .getTeamDetail({
 96→                  id: team.officialId,
 97→                  year,
 98→                  statsYear: year,
 99→                  eventsYear: year,
100→                  includeChampSeries: true,
101→                })
102→                .pipe(
103→                  Effect.either,
104→                  Effect.tap(() =>
105→                    Effect.sleep(
106→                      Duration.millis(config.delayBetweenRequestsMs),
107→                    ),
108→                  ),
109→                ),
110→            { concurrency: config.concurrency },
111→          );
112→
113→          const details = results
114→            .filter((r) => Either.isRight(r))
115→            .map((r) => r.right);
116→
117→          const durationMs = Date.now() - start;
118→          yield* saveJson(getOutputPath(year, "team-details"), details);
119→          yield* Effect.log(
120→            `     ✓ ${details.length} team details (${durationMs}ms)`,
121→          );
122→
123→          return { data: details, count: details.length, durationMs };
124→        });
125→
126→      /** Extracts players for a season. @see isCriticalError for error handling. */
127→      const extractPlayers = (year: number) =>
128→        Effect.gen(function* () {
129→          yield* Effect.log(`  👥 Extracting players for ${year}...`);
130→          const result = yield* pll
131→            .getPlayers({
132→              season: year,
133→              includeReg: true,
134→              includePost: true,
135→              includeZPP: true,
136→              limit: 1000,
137→            })
138→            .pipe(withTiming(), withRateLimitRetry(), Effect.either);
139→          if (Either.isLeft(result)) {
140→            yield* Effect.log(
141→              `     ✗ Failed [${result.left._tag}]: ${result.left.message}`,
142→            );
143→            if (isCriticalError(result.left)) {
144→              return yield* Effect.fail(result.left);
145→            }
146→            return emptyExtractResult([] as readonly PLLPlayer[]);
147→          }
148→          yield* saveJson(getOutputPath(year, "players"), result.right.data);
149→          yield* Effect.log(
150→            `     ✓ ${result.right.count} players (${result.right.durationMs}ms)`,
151→          );
152→          return result.right;
153→        });
154→
155→      /** Extracts advanced players for a season. @see isCriticalError for error handling. */
156→      const extractAdvancedPlayers = (year: number) =>
157→        Effect.gen(function* () {
158→          yield* Effect.log(`  🔬 Extracting advanced players for ${year}...`);
159→          const result = yield* pll
160→            .getAdvancedPlayers({
161→              year,
162→              limit: 500,
163→              league: "PLL",
164→            })
165→            .pipe(withTiming(), withRateLimitRetry(), Effect.either);
166→          if (Either.isLeft(result)) {
167→            yield* Effect.log(
168→              `     ✗ Failed [${result.left._tag}]: ${result.left.message}`,
169→            );
170→            if (isCriticalError(result.left)) {
171→              return yield* Effect.fail(result.left);
172→            }
173→            return emptyExtractResult([] as readonly PLLAdvancedPlayer[]);
174→          }
175→          yield* saveJson(
176→            getOutputPath(year, "advanced-players"),
177→            result.right.data,
178→          );
179→          yield* Effect.log(
180→            `     ✓ ${result.right.count} advanced players (${result.right.durationMs}ms)`,
181→          );
182→          return result.right;
183→        });
184→
185→      const extractPlayerDetails = (
186→        year: number,
187→        players: readonly PLLPlayer[],
188→      ) =>
189→        Effect.gen(function* () {
190→          yield* Effect.log(`  👤 Extracting player details for ${year}...`);
191→          const start = Date.now();
192→
193→          const playersWithSlug = players.filter(
194→            (p): p is PLLPlayer & { slug: string } => p.slug !== null,
195→          );
196→          yield* Effect.log(
197→            `     Processing ${playersWithSlug.length} players with slugs...`,
198→          );
199→
200→          const results = yield* Effect.forEach(
201→            playersWithSlug,
202→            (player) =>
203→              pll
204→                .getPlayerDetail({
205→                  slug: player.slug,
206→                  year,
207→                  statsYear: year,
208→                })
209→                .pipe(
210→                  Effect.either,
211→                  Effect.tap(() =>
212→                    Effect.sleep(
213→                      Duration.millis(config.delayBetweenRequestsMs),
214→                    ),
215→                  ),
216→                ),
217→            { concurrency: config.concurrency },
218→          );
219→
220→          const details = results
221→            .filter(Either.isRight)
222→            .map((r) => r.right)
223→            .filter((d): d is PLLPlayerDetail => d !== null);
224→
225→          const durationMs = Date.now() - start;
226→          yield* saveJson(getOutputPath(year, "player-details"), details);
227→          yield* Effect.log(
228→            `     ✓ ${details.length} player details (${durationMs}ms)`,
229→          );
230→
231→          return { data: details, count: details.length, durationMs };
232→        });
233→
234→      /** Extracts events for a season. @see isCriticalError for error handling. */
235→      const extractEvents = (year: number) =>
236→        Effect.gen(function* () {
237→          yield* Effect.log(`  🎮 Extracting events for ${year}...`);
238→          const result = yield* pll
239→            .getEvents({ year })
240→            .pipe(withTiming(), withRateLimitRetry(), Effect.either);
241→          if (Either.isLeft(result)) {
242→            yield* Effect.log(
243→              `     ✗ Failed [${result.left._tag}]: ${result.left.message}`,
244→            );
245→            if (isCriticalError(result.left)) {
246→              return yield* Effect.fail(result.left);
247→            }
248→            return emptyExtractResult([] as readonly PLLEvent[]);
249→          }
250→          yield* saveJson(getOutputPath(year, "events"), result.right.data);
251→          yield* Effect.log(
252→            `     ✓ ${result.right.count} events (${result.right.durationMs}ms)`,
253→          );
254→          return result.right;
255→        });
256→
257→      const extractEventDetails = (year: number, events: readonly PLLEvent[]) =>
258→        Effect.gen(function* () {
259→          yield* Effect.log(`  📋 Extracting event details for ${year}...`);
260→          const start = Date.now();
261→
262→          const completedEvents = events.filter(
263→            (e): e is PLLEvent & { slugname: string } =>
264→              e.eventStatus === 3 && e.slugname !== null,
265→          );
266→          yield* Effect.log(
267→            `     Processing ${completedEvents.length} completed events...`,
268→          );
269→
270→          const results = yield* Effect.forEach(
271→            completedEvents,
272→            (event) =>
273→              pll.getEventDetail({ slug: event.slugname }).pipe(
274→                Effect.either,
275→                Effect.tap(() =>
276→                  Effect.sleep(Duration.millis(config.delayBetweenRequestsMs)),
277→                ),
278→              ),
279→            { concurrency: config.concurrency },
280→          );
281→
282→          const details = results
283→            .filter(Either.isRight)
284→            .map((r) => r.right)
285→            .filter((d): d is PLLEventDetail => d !== null);
286→
287→          const durationMs = Date.now() - start;
288→          yield* saveJson(getOutputPath(year, "event-details"), details);
289→          yield* Effect.log(
290→            `     ✓ ${details.length} event details (${durationMs}ms)`,
291→          );
292→
293→          return { data: details, count: details.length, durationMs };
294→        });
295→
296→      /** Extracts standings for a season. @see isCriticalError for error handling. */
297→      const extractStandings = (year: number) =>
298→        Effect.gen(function* () {
299→          yield* Effect.log(`  📈 Extracting standings for ${year}...`);
300→          const result = yield* pll
301→            .getStandings({ year, champSeries: false })
302→            .pipe(withTiming(), withRateLimitRetry(), Effect.either);
303→          if (Either.isLeft(result)) {
304→            yield* Effect.log(
305→              `     ✗ Failed [${result.left._tag}]: ${result.left.message}`,
306→            );
307→            if (isCriticalError(result.left)) {
308→              return yield* Effect.fail(result.left);
309→            }
310→            return emptyExtractResult([] as readonly PLLTeamStanding[]);
311→          }
312→          yield* saveJson(getOutputPath(year, "standings"), result.right.data);
313→          yield* Effect.log(
314→            `     ✓ ${result.right.count} standings (${result.right.durationMs}ms)`,
315→          );
316→          return result.right;
317→        });
318→
319→      /** Extracts championship series standings for a season. @see isCriticalError for error handling. */
320→      const extractStandingsCS = (year: number) =>
321→        Effect.gen(function* () {
322→          yield* Effect.log(
323→            `  🏆 Extracting champ series standings for ${year}...`,
324→          );
325→          const result = yield* pll
326→            .getStandingsGraphQL({ year, champSeries: true })
327→            .pipe(withTiming(), withRateLimitRetry(), Effect.either);
328→          if (Either.isLeft(result)) {
329→            yield* Effect.log(
330→              `     ✗ Failed [${result.left._tag}]: ${result.left.message}`,
331→            );
332→            if (isCriticalError(result.left)) {
333→              return yield* Effect.fail(result.left);
334→            }
335→            return emptyExtractResult([] as readonly PLLGraphQLStanding[]);
336→          }
337→          yield* saveJson(
338→            getOutputPath(year, "standings-cs"),
339→            result.right.data,
340→          );
341→          yield* Effect.log(
342→            `     ✓ ${result.right.count} CS standings (${result.right.durationMs}ms)`,
343→          );
344→          return result.right;
345→        });
346→
347→      const extractYear = (
348→        year: number,
349→        options: IncrementalExtractOptions & { includeDetails?: boolean } = {},
350→      ) =>
351→        Effect.gen(function* () {
352→          const { includeDetails = true } = options;
353→
354→          yield* Effect.log(`\n${"=".repeat(50)}`);
355→          yield* Effect.log(`Extracting PLL ${year}`);
356→          yield* Effect.log("=".repeat(50));
357→
358→          let manifest = yield* manifestService.load;
359→
360→          const shouldExtract = (entity: keyof SeasonManifest): boolean => {
361→            const seasonManifest = manifestService.getSeasonManifest(
362→              manifest,
363→              year,
364→            );
365→            const entityStatus = seasonManifest[entity];
366→            return incremental.shouldExtract(entityStatus, year, options);
367→          };
368→
369→          if (shouldExtract("teams")) {
370→            const result = yield* extractTeams(year);
371→            manifest = manifestService.markComplete(
372→              manifest,
373→              year,
374→              "teams",
375→              result.count,
376→              result.durationMs,
377→            );
378→          } else {
379→            yield* Effect.log("  📊 Teams: skipped (already extracted)");
380→          }
381→
382→          if (includeDetails && shouldExtract("teamDetails")) {
383→            const teamsPath = getOutputPath(year, "teams");
384→            const teamsData = yield* readJsonFile(teamsPath);
385→            const teams = yield* Effect.try({
386→              try: () => JSON.parse(teamsData) as unknown,
387→              catch: (e) =>
388→                new Error(
389→                  `Failed to parse teams JSON for ${year}: ${String(e)}`,
390→                ),
391→            }).pipe(
392→              Effect.flatMap((parsed) =>
393→                Schema.decodeUnknown(Schema.Array(PLLTeam))(parsed),
394→              ),
395→              Effect.tap(() =>
396→                Effect.log(`Successfully decoded teams for ${year}`),
397→              ),
398→              Effect.catchAll((error) =>
399→                Effect.zipRight(
400→                  Effect.logError(`Failed to decode teams for ${year}`, error),
401→                  Effect.succeed([]),
402→                ),
403→              ),
404→            );
405→            if (teams.length > 0) {
406→              const result = yield* extractTeamDetails(year, teams);
407→              manifest = manifestService.markComplete(
408→                manifest,
409→                year,
410→                "teamDetails",
411→                result.count,
412→                result.durationMs,
413→              );
414→            }
415→          } else if (includeDetails) {
416→            yield* Effect.log("  🏟️ Team details: skipped (already extracted)");
417→          }
418→
419→          if (shouldExtract("players")) {
420→            const result = yield* extractPlayers(year);
421→            manifest = manifestService.markComplete(
422→              manifest,
423→              year,
424→              "players",
425→              result.count,
426→              result.durationMs,
427→            );
428→          } else {
429→            yield* Effect.log("  👥 Players: skipped (already extracted)");
430→          }
431→
432→          if (shouldExtract("advancedPlayers")) {
433→            const result = yield* extractAdvancedPlayers(year);
434→            manifest = manifestService.markComplete(
435→              manifest,
436→              year,
437→              "advancedPlayers",
438→              result.count,
439→              result.durationMs,
440→            );
441→          } else {
442→            yield* Effect.log(
443→              "  🔬 Advanced players: skipped (already extracted)",
444→            );
445→          }
446→
447→          if (includeDetails && shouldExtract("playerDetails")) {
448→            const playersPath = getOutputPath(year, "players");
449→            const playersData = yield* readJsonFile(playersPath);
450→            const players = yield* Effect.try({
451→              try: () => JSON.parse(playersData) as unknown,
452→              catch: (e) =>
453→                new Error(
454→                  `Failed to parse players JSON for ${year}: ${String(e)}`,
455→                ),
456→            }).pipe(
457→              Effect.flatMap((parsed) =>
458→                Schema.decodeUnknown(Schema.Array(PLLPlayer))(parsed),
459→              ),
460→              Effect.tap(() =>
461→                Effect.log(`Successfully decoded players for ${year}`),
462→              ),
463→              Effect.catchAll((error) =>
464→                Effect.zipRight(
465→                  Effect.logError(
466→                    `Failed to decode players for ${year}`,
467→                    error,
468→                  ),
469→                  Effect.succeed([]),
470→                ),
471→              ),
472→            );
473→            if (players.length > 0) {
474→              const result = yield* extractPlayerDetails(year, players);
475→              manifest = manifestService.markComplete(
476→                manifest,
477→                year,
478→                "playerDetails",
479→                result.count,
480→                result.durationMs,
481→              );
482→            }
483→          } else if (includeDetails) {
484→            yield* Effect.log(
485→              "  👤 Player details: skipped (already extracted)",
486→            );
487→          }
488→
489→          if (shouldExtract("events")) {
490→            const result = yield* extractEvents(year);
491→            manifest = manifestService.markComplete(
492→              manifest,
493→              year,
494→              "events",
495→              result.count,
496→              result.durationMs,
497→            );
498→          } else {
499→            yield* Effect.log("  🎮 Events: skipped (already extracted)");
500→          }
501→
502→          if (includeDetails && shouldExtract("eventDetails")) {
503→            const eventsPath = getOutputPath(year, "events");
504→            const eventsData = yield* readJsonFile(eventsPath);
505→            const events = yield* Effect.try({
506→              try: () => JSON.parse(eventsData) as unknown,
507→              catch: (e) =>
508→                new Error(
509→                  `Failed to parse events JSON for ${year}: ${String(e)}`,
510→                ),
511→            }).pipe(
512→              Effect.flatMap((parsed) =>
513→                Schema.decodeUnknown(Schema.Array(PLLEvent))(parsed),
514→              ),
515→              Effect.tap(() =>
516→                Effect.log(`Successfully decoded events for ${year}`),
517→              ),
518→              Effect.catchAll((error) =>
519→                Effect.zipRight(
520→                  Effect.logError(`Failed to decode events for ${year}`, error),
521→                  Effect.succeed([]),
522→                ),
523→              ),
524→            );
525→            if (events.length > 0) {
526→              const result = yield* extractEventDetails(year, events);
527→              manifest = manifestService.markComplete(
528→                manifest,
529→                year,
530→                "eventDetails",
531→                result.count,
532→                result.durationMs,
533→              );
534→            }
535→          } else if (includeDetails) {
536→            yield* Effect.log(
537→              "  📋 Event details: skipped (already extracted)",
538→            );
539→          }
540→
541→          if (shouldExtract("standings")) {
542→            const result = yield* extractStandings(year);
543→            manifest = manifestService.markComplete(
544→              manifest,
545→              year,
546→              "standings",
547→              result.count,
548→              result.durationMs,
549→            );
550→          } else {
551→            yield* Effect.log("  📈 Standings: skipped (already extracted)");
552→          }
553→
554→          if (shouldExtract("standingsCS")) {
555→            const result = yield* extractStandingsCS(year);
556→            manifest = manifestService.markComplete(
557→              manifest,
558→              year,
559→              "standingsCS",
560→              result.count,
561→              result.durationMs,
562→            );
563→          } else {
564→            yield* Effect.log("  🏆 CS Standings: skipped (already extracted)");
565→          }
566→
567→          // Save manifest once at end of season
568→          yield* manifestService.save(manifest);
569→
570→          return manifest;
571→        });
572→
573→      const extractAll = (
574→        options: IncrementalExtractOptions & { includeDetails?: boolean } = {},
575→      ) =>
576→        Effect.gen(function* () {
577→          yield* Effect.log("🏈 PLL Full Extraction");
578→          yield* Effect.log(`Output directory: ${config.outputDir}`);
579→
580→          for (const year of PLL_YEARS) {
581→            yield* extractYear(year, options);
582→            yield* Effect.sleep(Duration.millis(config.delayBetweenBatchesMs));
583→          }
584→
585→          const manifest = yield* manifestService.load;
586→          yield* Effect.log("\n" + "=".repeat(50));
587→          yield* Effect.log("EXTRACTION COMPLETE");
588→          yield* Effect.log("=".repeat(50));
589→
590→          return manifest;
591→        });
592→
593→      return {
594→        extractTeams,
595→        extractTeamDetails,
596→        extractPlayers,
597→        extractAdvancedPlayers,
598→        extractPlayerDetails,
599→        extractEvents,
600→        extractEventDetails,
601→        extractStandings,
602→        extractStandingsCS,
603→        extractYear,
604→        extractAll,
605→        PLL_YEARS,
606→      };
607→    }),
608→    dependencies: [
609→      Layer.mergeAll(
610→        PLLClient.Default,
611→        ExtractConfigService.Default,
612→        PLLManifestService.Default,
613→        IncrementalExtractionService.Default,
614→        BunContext.layer,
615→      ),
616→    ],
617→  },
618→) {}
