import { cn } from "@laxdb/ui/lib/utils";

function DashedLine({ className }: { className?: string }) {
  return (
    <div
      className={cn("absolute inset-y-0 w-px", className)}
      style={{ maskImage: "linear-gradient(transparent, white 20%, white 80%, transparent)" }}
    >
      <svg aria-hidden="true" className="h-full w-full" preserveAspectRatio="none">
        <line
          className="stroke-border"
          strokeDasharray="3 3"
          strokeWidth="2"
          x1="0"
          x2="0"
          y1="0"
          y2="100%"
        />
      </svg>
    </div>
  );
}

function NotFound({
  code = "404",
  title = "Page not found",
  description = "The page you\u2019re looking for doesn\u2019t exist or has been moved.",
  children,
  className,
}: {
  code?: string;
  title?: string;
  description?: string;
  children?: React.ReactNode;
  className?: string;
}) {
  return (
    <main
      className={cn(
        "relative mx-auto flex min-h-[80vh] max-w-6xl flex-col items-center justify-center px-4",
        className,
      )}
    >
      <div className="pointer-events-none absolute inset-0">
        <DashedLine />
        <DashedLine className="right-0" />
      </div>

      <p className="text-[11px] tracking-[0.2em] text-subtle uppercase">{code}</p>
      <h1 className="mt-3 text-center font-serif text-5xl text-foreground italic sm:text-7xl">
        {title}
      </h1>
      <p className="mt-4 max-w-md text-center text-base text-balance text-muted-foreground">
        {description}
      </p>

      {children && <div className="mt-8 flex items-center gap-4">{children}</div>}
    </main>
  );
}

function NotFoundAction({ className, ...props }: React.ComponentProps<"a">) {
  return (
    <a
      className={cn(
        "inline-flex cursor-pointer items-center justify-center gap-1 rounded-md border-b-[1.5px] border-foreground/20 bg-foreground px-5 py-3 text-sm leading-4 font-medium tracking-wide text-background shadow-[0_0_0_2px_rgba(0,0,0,0.04),0_0_14px_0_rgba(255,255,255,0.19)] transition-all duration-200 ease-in-out hover:opacity-90",
        className,
      )}
      {...props}
    />
  );
}

function NotFoundSecondaryAction({ className, ...props }: React.ComponentProps<"button">) {
  return (
    <button
      className={cn(
        "inline-flex cursor-pointer items-center justify-center gap-1.5 rounded-md px-5 py-3 text-sm leading-4 font-medium tracking-wide text-muted-foreground transition-all duration-200 ease-in-out hover:text-foreground",
        className,
      )}
      type="button"
      {...props}
    />
  );
}

export { NotFound, NotFoundAction, NotFoundSecondaryAction };

/// <reference types="vite/client" />

import { NotFound, NotFoundSecondaryAction } from "@laxdb/ui/components/not-found";
import { ThemeProvider } from "@laxdb/ui/components/theme-provider";
import { createRootRoute, HeadContent, Link, Outlet, Scripts } from "@tanstack/react-router";

import Footer from "@/components/footer";
import { NavBar } from "@/components/navbar";
import globalsCss from "@/globals.css?url";
import { siteConfig } from "@/site";

export const Route = createRootRoute({
  head: () => ({
    meta: [
      { charSet: "utf-8" },
      { name: "viewport", content: "width=device-width, initial-scale=1" },
      { title: siteConfig.name },
      { name: "description", content: siteConfig.description },
      { name: "keywords", content: "Lacrosse, Statistics, PLL, NLL, Analytics, Database" },
      { property: "og:type", content: "website" },
      { property: "og:locale", content: "en_US" },
      { property: "og:url", content: siteConfig.url },
      { property: "og:title", content: siteConfig.name },
      { property: "og:description", content: siteConfig.description },
      { property: "og:site_name", content: siteConfig.name },
      { name: "twitter:card", content: "summary_large_image" },
      { name: "twitter:title", content: siteConfig.name },
      { name: "twitter:description", content: siteConfig.description },
    ],
    links: [
      { rel: "stylesheet", href: globalsCss },
      { rel: "icon", href: "/favicon.svg", type: "image/svg+xml" },
    ],
  }),
  component: RootComponent,
  notFoundComponent: NotFoundComponent,
});

function RootComponent() {
  return (
    <RootDocument>
      <NavBar />
      <Outlet />
      <Footer />
    </RootDocument>
  );
}

function RootDocument({ children }: { children: React.ReactNode }) {
  return (
    <html lang="en" suppressHydrationWarning>
      <head>
        <HeadContent />
      </head>
      <body className="min-h-screen overflow-x-hidden scroll-auto bg-background text-foreground antialiased">
        <ThemeProvider attribute="class" defaultTheme="system" enableSystem>
          {children}
        </ThemeProvider>
        <Scripts />
      </body>
    </html>
  );
}

function NotFoundComponent() {
  return (
    <NotFound>
      <NotFoundSecondaryAction
        onClick={() => {
          window.history.back();
        }}
      >
        &larr; Go Back
      </NotFoundSecondaryAction>
      <Link
        className="inline-flex cursor-pointer items-center justify-center gap-1 rounded-md border-b-[1.5px] border-foreground/20 bg-foreground px-5 py-3 text-sm leading-4 font-medium tracking-wide text-background shadow-[0_0_0_2px_rgba(0,0,0,0.04),0_0_14px_0_rgba(255,255,255,0.19)] transition-all duration-200 ease-in-out hover:opacity-90"
        to="/"
      >
        Go Home
      </Link>
    </NotFound>
  );
}

