+    font-family: var(--font-serif);
+    font-style: italic;
+    font-weight: 400;
+    font-size: calc(1rem + 1px);
+    margin-top: 2rem;
+    margin-bottom: 2rem;
+  }
+
+  & em,
+  & i {
+    font-family: var(--font-serif);
+    font-style: italic;
+    font-size: calc(1rem + 1px);
+    font-weight: 500;
+  }
+
+  & a {
+    color: inherit;
+    text-decoration: none;
+  }
+
+  & a:hover {
+    text-decoration: underline;
+  }
+}
+
+@keyframes gridMovingLine {
+  0% {
+    transform: translateX(100%);
+    opacity: 0;
+  }
+  20% {
+    opacity: 100;
+  }
+  100% {
