@import "tailwindcss";
@import "tw-animate-css";
/* @import "@fontsource-variable/dm-sans"; — archived, using system font stack */
@plugin "@tailwindcss/typography";

@font-face {
  font-family: "Newsreader";
  src: url(./fonts/Newsreader.ttf) format("truetype");
  font-weight: 100 900;
  font-style: normal italic;
  font-display: swap;
}

@theme {
  --font-sans: "Helvetica Neue", "Helvetica", "Inter", -apple-system, sans-serif;
  --font-serif: "Newsreader", "Times", serif;
  --font-mono: "Newsreader", serif;

  --radius-lg: var(--radius);
  --radius-md: calc(var(--radius) - 2px);
  --radius-sm: calc(var(--radius) - 4px);

  --breakpoint-xs: 476px;
  --breakpoint-toast-mobile: 600px;

  --color-background: oklch(var(--background));
  --color-foreground: oklch(var(--foreground));
  --color-card: oklch(var(--card));
  --color-card-foreground: oklch(var(--card-foreground));
  --color-popover: oklch(var(--popover));
  --color-popover-foreground: oklch(var(--popover-foreground));
  --color-primary: oklch(var(--primary));
  --color-primary-foreground: oklch(var(--primary-foreground));
  --color-secondary: oklch(var(--secondary));
  --color-secondary-foreground: oklch(var(--secondary-foreground));
  --color-muted: oklch(var(--muted));
  --color-muted-foreground: oklch(var(--muted-foreground));
  --color-accent: oklch(var(--accent));
  --color-accent-foreground: oklch(var(--accent-foreground));
  --color-destructive: oklch(var(--destructive));
  --color-destructive-foreground: oklch(var(--destructive-foreground));
  --color-success: oklch(var(--success));
  --color-success-foreground: oklch(var(--success-foreground));
  --color-warning: oklch(var(--warning));
  --color-warning-foreground: oklch(var(--warning-foreground));
  --color-subtle: oklch(var(--subtle));
  --color-border: oklch(var(--border));
  --color-border-strong: oklch(var(--border-strong));
  --color-bullet: oklch(var(--bullet));
  --color-orange: oklch(var(--orange));
  --color-orange-foreground: oklch(var(--orange-foreground));
  --color-input: oklch(var(--input));
  --color-ring: oklch(var(--ring));
  --color-chart-1: oklch(var(--chart-1));
  --color-chart-2: oklch(var(--chart-2));
  --color-chart-3: oklch(var(--chart-3));
  --color-chart-4: oklch(var(--chart-4));
  --color-chart-5: oklch(var(--chart-5));
  --color-sidebar: oklch(var(--sidebar));
  --color-sidebar-foreground: oklch(var(--sidebar-foreground));
  --color-sidebar-primary: oklch(var(--sidebar-primary));
  --color-sidebar-primary-foreground: oklch(var(--sidebar-primary-foreground));
  --color-sidebar-accent: oklch(var(--sidebar-accent));
  --color-sidebar-accent-foreground: oklch(var(--sidebar-accent-foreground));
  --color-sidebar-border: oklch(var(--sidebar-border));
  --color-sidebar-ring: oklch(var(--sidebar-ring));

  --ease-smooth-bounce: cubic-bezier(0.16, 1, 0.3, 1.03);

  --animate-dashes: dashes 0.8s linear infinite;
  --animate-dashes-reverse: dashes 0.8s linear infinite reverse;
  --animate-hover: hover 4s cubic-bezier(0.45, 0, 0.55, 1) infinite;

  @keyframes dashes {
    0% {
      stroke-dashoffset: -7;
    }
    100% {
      stroke-dashoffset: 0;
    }
  }
  @keyframes hover {
    0%,
    100% {
      transform: translateY(0);
    }
    50% {
      transform: translateY(-10px);
    }
  }
}

:root {
  --background: 0.94 0.007 70;
  --foreground: 0.17 0 0;
  --card: 1 0 0;
  --card-foreground: 0.145 0 0;
  --popover: 1 0 0;
  --popover-foreground: 0.145 0 0;
  --primary: 0.23 0.006 70;
  --primary-hover: 0.55 0.2 260;
  --primary-active: 0.48 0.18 262;
  --primary-foreground: 1 0 0;
  --primary-gradient-start: 0.62 0.21 258;
  --primary-gradient-end: 0.4 0.16 265;
  --secondary: 0.97 0 0;
  --secondary-foreground: 0.205 0 0;
  --muted: 0.97 0 0;
  --muted-foreground: 0.51 0 0;
  --accent: 0.91 0 0;
  --accent-foreground: 0.17 0 0;
  --subtle: 0.63 0 0;
  --destructive: 0.58 0.22 27;
  --destructive-foreground: 0.98 0 0;
  --success: 0.55 0.15 145;
  --success-foreground: 0.98 0 0;
  --warning: 0.75 0.18 70;
  --warning-foreground: 0.98 0 0;
  --orange: 0.55 0.12 50;
  --orange-foreground: 0.98 0 0;
  --border: 0.93 0 0;
  --border-strong: 0.89 0 0;
  --bullet: 0.82 0 0;
  --input: 0.93 0 0;
  --ring: 0.708 0 0;
  --hover: var(--primary-60);
  --chart-1: 0.809 0.105 251.813;
  --chart-2: 0.623 0.214 259.815;
  --chart-3: 0.546 0.245 262.881;
  --chart-4: 0.488 0.243 264.376;
  --chart-5: 0.424 0.199 265.638;
  --radius: 0.45rem;
  --sidebar-background: 0.98 0 0;
  --sidebar-foreground: 0.145 0 0;
  --sidebar-primary: 0.205 0 0;
  --sidebar-primary-foreground: 0.985 0 0;
  --sidebar-accent: 0.97 0 0;
  --sidebar-accent-foreground: 0.205 0 0;
  --sidebar-border: 0.922 0 0;
  --sidebar-ring: 0.708 0 0;
  --sidebar: 0.985 0 0;
}

.dark {
  --background: 0.23 0.006 70;
  --foreground: 0.985 0 0;
  --card: 0.205 0 0;
  --card-foreground: 0.985 0 0;
  --popover: 0.205 0 0;
  --popover-foreground: 0.985 0 0;
  --primary: 0.94 0.007 70;
  --primary-hover: 0.55 0.2 260;
  --primary-active: 0.48 0.18 262;
  --primary-foreground: 0.45 0.003 70;
  --primary-gradient-start: 0.62 0.21 258;
  --primary-gradient-end: 0.72 0.17 252;
  --secondary: 0.269 0 0;
  --secondary-foreground: 0.985 0 0;
  --muted: 0.269 0 0;
  --muted-foreground: 0.73 0 0;
  --accent: 0.3 0 0;
  --accent-foreground: 0.985 0 0;
  --subtle: 0.51 0 0;
  --destructive: 0.704 0.191 22.216;
  --destructive-foreground: 0.98 0 0;
  --success: 0.65 0.18 145;
  --success-foreground: 0.15 0 0;
  --warning: 0.8 0.16 70;
  --warning-foreground: 0.15 0 0;
  --orange: 0.65 0.14 50;
  --orange-foreground: 0.15 0 0;
  --border: 0.32 0 0;
  --border-strong: 0.4 0 0;
  --bullet: 0.4 0 0;
  --input: 0.32 0 0;
  --ring: 0.556 0 0;
  --hover: var(--primary-20);
  --chart-1: 0.809 0.105 251.813;
  --chart-2: 0.623 0.214 259.815;
  --chart-3: 0.546 0.245 262.881;
  --chart-4: 0.488 0.243 264.376;
  --chart-5: 0.424 0.199 265.638;
  --sidebar-background: 0.205 0 0;
  --sidebar-foreground: 0.985 0 0;
  --sidebar-primary: 0.488 0.243 264.376;
  --sidebar-primary-foreground: 0.985 0 0;
  --sidebar-accent: 0.269 0 0;
  --sidebar-accent-foreground: 0.985 0 0;
  --sidebar-border: 1 0 0 / 10%;
  --sidebar-ring: 0.556 0 0;
  --sidebar: 0.205 0 0;
}

@layer base {
  *,
  ::after,
  ::before,
  ::backdrop,
  ::file-selector-button {
    border-color: var(--color-border, currentColor);
  }

  * {
    @apply outline-ring/50;
  }

  html {
    scrollbar-gutter: stable;
    scrollbar-width: thin;
  }

  body {
    background-color: var(--color-background);
    color: var(--color-foreground);
    font-family: var(--font-sans);
    font-size: 16px;
    line-height: 28px;
    text-rendering: optimizeLegibility;
    -webkit-font-smoothing: antialiased;
    -moz-osx-font-smoothing: grayscale;
  }

  h2 {
    font-family: var(--font-serif);
    font-style: italic;
  }

  em,
  i {
    font-family: var(--font-serif);
    font-style: italic;
    font-size: calc(1rem + 1px);
  }

  .dark body {
    color-scheme: dark;
  }

  .dark .mask canvas {
    filter: invert(1);
  }

  div {
    @apply relative;
  }

  textarea {
    field-sizing: content;
    resize: none;
  }

  .error {
    color: rgb(255, 0, 0);
    font-size: smaller;
  }

  li {
    list-style: none;
  }

  input[type="number"]::-webkit-outer-spin-button,
  input[type="number"]::-webkit-inner-spin-button {
    -webkit-appearance: none;
    margin: 0;
  }

  input[type="number"] {
    -moz-appearance: textfield;
    appearance: textfield;
  }
}

.mask {
  mask-image: radial-gradient(rgba(0, 0, 0, 1) 0%, transparent 80%);
}

.prose-blog {
  --tw-prose-body: oklch(var(--foreground) / 0.75);
  --tw-prose-headings: var(--color-foreground);
  --tw-prose-lead: var(--color-muted-foreground);
  --tw-prose-links: var(--color-foreground);
  --tw-prose-bold: var(--color-foreground);
  --tw-prose-counters: var(--color-muted-foreground);
  --tw-prose-bullets: var(--color-bullet);
  --tw-prose-hr: var(--color-border);
  --tw-prose-quotes: var(--color-foreground);
  --tw-prose-quote-borders: var(--color-border-strong);
  --tw-prose-captions: var(--color-muted-foreground);
  --tw-prose-code: var(--color-foreground);
  --tw-prose-pre-code: #ededed;
  --tw-prose-pre-bg: #1a1a1a;
  --tw-prose-th-borders: var(--color-border-strong);
  --tw-prose-td-borders: var(--color-border);

  font-size: 16px;
  line-height: 28px;
  text-rendering: optimizeLegibility;
  -webkit-font-smoothing: antialiased;
  -moz-osx-font-smoothing: grayscale;

  & h2 {
    font-family: var(--font-serif);
    font-style: italic;
    font-weight: 400;
    font-size: calc(1rem + 1px);
    margin-top: 2rem;
    margin-bottom: 2rem;
  }

  & em,
  & i {
    font-family: var(--font-serif);
    font-style: italic;
    font-size: calc(1rem + 1px);
    font-weight: 500;
  }

  & a {
    color: inherit;
    text-decoration: none;
  }

  & a:hover {
    text-decoration: underline;
  }
}

@keyframes gridMovingLine {
  0% {
    transform: translateX(100%);
    opacity: 0;
  }
  20% {
    opacity: 100;
  }
  100% {
    transform: translateX(-100%);
    opacity: 100;
  }
}

@layer utilities {
  .text-balance {
    text-wrap: balance;
  }

  .no-scrollbar {
    -ms-overflow-style: none;
    scrollbar-width: none;
  }

  .no-scrollbar::-webkit-scrollbar {
    display: none;
  }

  .text-label {
    letter-spacing: 0.1em;
    text-transform: uppercase;
    color: var(--color-subtle);
  }

  .corners-border {
    position: relative;
  }

  .corners-border::before {
    content: "";
    position: absolute;
    inset: -1px;
    pointer-events: none;
    transition: opacity 150ms ease;
    opacity: 0.1;
    background:
      linear-gradient(currentColor, currentColor) top left / 5px 1px no-repeat,
      linear-gradient(currentColor, currentColor) top left / 1px 5px no-repeat,
      linear-gradient(currentColor, currentColor) top right / 5px 1px no-repeat,
      linear-gradient(currentColor, currentColor) top right / 1px 5px no-repeat,
      linear-gradient(currentColor, currentColor) bottom left / 5px 1px no-repeat,
      linear-gradient(currentColor, currentColor) bottom left / 1px 5px no-repeat,
      linear-gradient(currentColor, currentColor) bottom right / 5px 1px no-repeat,
      linear-gradient(currentColor, currentColor) bottom right / 1px 5px no-repeat;
  }

  .corners-border:hover::before {
    opacity: 0.3;
  }

  .corners-border-fixed::before {
    opacity: 0.6;
  }
}

