import { FadeDiv } from "@/components/fade";
import { formatPublishedDate } from "@/lib/date";
import type { ChangelogEntry } from "@/lib/changelog";
import { CATEGORY_LABELS } from "@/lib/changelog";

export function ChangelogEntryCard({ entry }: { entry: ChangelogEntry }) {
  return (
    <FadeDiv className="flex gap-6 md:gap-10">
      {/* Date column — desktop only */}
      <div className="hidden w-[140px] shrink-0 pt-0.5 text-right md:block">
        <time className="text-xs whitespace-nowrap text-subtle" dateTime={entry.date}>
          {formatPublishedDate(entry.date)}
        </time>
      </div>

      {/* Timeline dot + line */}
      <div className="flex flex-col items-center">
        <div className="size-2.5 shrink-0 rounded-full bg-orange ring-4 ring-background" />
        <div
          className="w-px flex-1"
          style={{
            maskImage: "linear-gradient(white, white 80%, transparent)",
          }}
        >
          <svg aria-hidden="true" className="h-full w-full" preserveAspectRatio="none">
            <line
              className="stroke-border"
              strokeDasharray="3 3"
              strokeWidth="2"
              x1="0"
              x2="0"
              y1="0"
              y2="100%"
            />
          </svg>
        </div>
      </div>

      {/* Content */}
      <div className="min-w-0 flex-1 pb-16">
        {/* Mobile date */}
        <time
          className="mb-2 block text-xs text-subtle md:hidden"
          dateTime={entry.date}
        >
          {formatPublishedDate(entry.date)}
        </time>

        <h2 className="font-serif text-xl text-foreground italic md:text-2xl">{entry.title}</h2>
        <span className="text-label mt-1 inline-block text-xs">{CATEGORY_LABELS[entry.category]}</span>

        <p className="mt-3 text-[15px] leading-relaxed text-muted-foreground">{entry.summary}</p>

        <div className="mt-4 space-y-3 text-[15px] leading-relaxed text-foreground">
          {entry.body.map((paragraph) => (
            <p key={paragraph.slice(0, 40)}>{paragraph}</p>
          ))}
        </div>

        {entry.highlights && (
          <ul className="mt-4 space-y-3">
            {entry.highlights.map((highlight) => (
              <li className="flex gap-3 text-[15px] leading-relaxed" key={highlight}>
                <span className="mt-1.5 block size-1.5 shrink-0 rounded-full bg-bullet" />
                <span className="text-foreground">{highlight}</span>
              </li>
            ))}
          </ul>
        )}
      </div>
    </FadeDiv>
  );
}

