import { createFileRoute, useNavigate } from "@tanstack/react-router";

import { FadeContainer, FadeDiv } from "@/components/fade";
import { ChangelogEntryCard } from "@/components/changelog-entry";
import {
  CHANGELOG_FILTERS,
  changelogEntries,
  filterEntries,
  type ChangelogFilterKey,
} from "@/lib/changelog";

export const Route = createFileRoute("/changelog")({
  validateSearch: (search: Record<string, unknown>) => ({
    filter: (CHANGELOG_FILTERS.some((f) => f.key === search.filter)
      ? search.filter
      : undefined) as ChangelogFilterKey | undefined,
  }),
  component: ChangelogPage,
});

function ChangelogPage() {
  const { filter: searchFilter } = Route.useSearch();
  const filter = searchFilter ?? "all";
  const navigate = useNavigate();

  const sorted = [...changelogEntries].sort(
    (a, b) => new Date(b.date).getTime() - new Date(a.date).getTime(),
  );
  const filtered = filterEntries(sorted, filter);

  return (
    <FadeContainer>
      <main className="mx-auto max-w-4xl px-4 py-16 md:py-32">
        <FadeDiv>
          <header className="mb-10">
            <h1 className="font-serif text-2xl text-foreground italic">Changelog</h1>
            <nav className="mt-5 flex items-baseline gap-5 border-b border-border">
              {CHANGELOG_FILTERS.map((f) => {
                const active = filter === f.key;
                return (
                  <button
                    key={f.key}
                    type="button"
                    onClick={() =>
                      navigate({
                        to: "/changelog",
                        search: { filter: f.key === "all" ? undefined : f.key },
                      })
                    }
                    className={`relative pb-2.5 text-sm tracking-wide transition-colors ${
                      active ? "text-foreground" : "text-subtle hover:text-muted-foreground"
                    }`}
                  >
                    {f.label}
                    {active && (
                      <span className="absolute inset-x-0 -bottom-px h-px bg-foreground" />
                    )}
                  </button>
                );
              })}
            </nav>
          </header>
        </FadeDiv>

        {/* Timeline */}
        <div className="mt-12">
          {filtered.length === 0 ? (
            <FadeDiv>
              <p className="text-muted-foreground">No entries in this category yet.</p>
            </FadeDiv>
          ) : (
            filtered.map((entry) => <ChangelogEntryCard entry={entry} key={entry.id} />)
          )}
        </div>
      </main>
    </FadeContainer>
  );
}

/// <reference types="vite/client" />

import { NotFound, NotFoundAction, NotFoundSecondaryAction } from "@laxdb/ui/components/not-found";
import { ThemeProvider } from "@laxdb/ui/components/theme-provider";
import { createRootRoute, HeadContent, Link, Outlet, Scripts } from "@tanstack/react-router";

import Footer from "@/components/footer";
import { NavBar } from "@/components/navbar";
import globalsCss from "@/globals.css?url";
import { siteConfig } from "@/site";

export const Route = createRootRoute({
  head: () => ({
    meta: [
      { charSet: "utf-8" },
      { name: "viewport", content: "width=device-width, initial-scale=1" },
      { title: siteConfig.name },
      { name: "description", content: siteConfig.description },
      { name: "keywords", content: "Lacrosse, Statistics, PLL, NLL, Analytics, Database" },
      { property: "og:type", content: "website" },
      { property: "og:locale", content: "en_US" },
      { property: "og:url", content: siteConfig.url },
      { property: "og:title", content: siteConfig.name },
      { property: "og:description", content: siteConfig.description },
      { property: "og:site_name", content: siteConfig.name },
      { name: "twitter:card", content: "summary_large_image" },
      { name: "twitter:title", content: siteConfig.name },
      { name: "twitter:description", content: siteConfig.description },
    ],
    links: [
      { rel: "stylesheet", href: globalsCss },
      { rel: "icon", href: "/favicon.svg", type: "image/svg+xml" },
    ],
  }),
  component: RootComponent,
  notFoundComponent: NotFoundComponent,
});

function RootComponent() {
  return (
    <RootDocument>
      <NavBar />
      <Outlet />
      <Footer />
    </RootDocument>
  );
}

function RootDocument({ children }: { children: React.ReactNode }) {
  return (
    <html lang="en" suppressHydrationWarning>
      <head>
        <HeadContent />
      </head>
      <body className="min-h-screen overflow-x-hidden scroll-auto bg-background text-foreground antialiased">
        <ThemeProvider attribute="class" defaultTheme="system" enableSystem>
          {children}
        </ThemeProvider>
        <Scripts />
      </body>
    </html>
  );
}

function NotFoundComponent() {
  return (
    <NotFound>
      <NotFoundSecondaryAction
        onClick={() => {
          window.history.back();
        }}
      >
        &larr; Go Back
      </NotFoundSecondaryAction>
      <NotFoundAction render={<Link to="/" />}>Go Home</NotFoundAction>
    </NotFound>
  );
}

