 1→import { defineCollection, defineConfig } from "@content-collections/core";
 2→import { compileMDX } from "@content-collections/mdx";
 3→import rehypeAutolinkHeadings from "rehype-autolink-headings";
 4→import { rehypePrettyCode } from "rehype-pretty-code";
 5→import rehypeSlug from "rehype-slug";
 6→import remarkGfm from "remark-gfm";
 7→import { z } from "zod";
 8→
 9→import { toSlug } from "./src/lib/slug";
10→
11→/**
12→ * Extract [[wiki-links]] from content
13→ */
14→function extractWikiLinks(content: string): string[] {
15→  const wikiLinkRegex = /\[\[([^\]]+)\]\]/g;
16→  const links: string[] = [];
17→  let match;
18→  while ((match = wikiLinkRegex.exec(content)) !== null) {
19→    if (match[1]) links.push(match[1]);
20→  }
21→  return [...new Set(links)];
22→}
23→
24→/**
25→ * Transform [[wiki-links]] to markdown links in content
26→ */
27→function transformWikiLinks(content: string): string {
28→  return content.replaceAll(/\[\[([^\]]+)\]\]/g, (_, label: string) => {
29→    return `[${label}](/content/${toSlug(label)})`;
30→  });
31→}
32→
33→const posts = defineCollection({
34→  name: "posts",
35→  directory: "./src/content",
36→  include: "**/*.mdx",
37→  schema: z.object({
38→    title: z.string(),
39→    published: z.iso.date(),
40→    draft: z.boolean().default(false),
41→    description: z.string().optional(),
42→    authors: z.array(z.string()).optional(),
43→    tags: z.array(z.string()).optional(),
44→    content: z.string(),
45→  }),
46→  transform: async (document, context) => {
47→    // Extract wiki links before transformation
48→    const wikiLinks = extractWikiLinks(document.content);
49→
50→    // Transform wiki links in content before MDX compilation
51→    const transformedContent = transformWikiLinks(document.content);
52→    const transformedDocument = { ...document, content: transformedContent };
53→
54→    const mdx = await compileMDX(context, transformedDocument, {
55→      remarkPlugins: [remarkGfm],
56→      rehypePlugins: [
57→        rehypeSlug,
58→        [rehypeAutolinkHeadings, { behavior: "wrap" }],
59→        [rehypePrettyCode, { theme: "github-dark" }],
60→      ],
61→    });
62→
63→    const headerImageMatch = document.content.match(/!\[([^\]]*)\]\(([^)]+)\)/);
64→    const headerImage = headerImageMatch ? headerImageMatch[2] : undefined;
65→
66→    const excerpt =
67→      document.content
68→        .replace(/^---[\s\S]*?---\s*/, "")
69→        .replace(/^!\[[^\]]*]\([^)]+\)\s*/, "")
70→        .split("\n\n")[0]
71→        ?.replaceAll(/\[\[([^\]]+)\]\]/g, "$1")
72→        .trim() ?? undefined;
73→
74→    return {
75→      ...document,
76→      slug: document._meta.path,
77→      excerpt,
78→      headerImage,
79→      wikiLinks, // Links for graph view
80→      mdx,
81→    };
82→  },
83→});
84→
85→export default defineConfig({
86→  collections: [posts],
87→});
