 34→// Domain
 35→const PRODUCTION = "laxdb.io";
 36→const DEV = "dev.laxdb.io";
 37→
 38→const getDomain = (subdomain?: string) => {
 39→  const prefix = subdomain ? `${subdomain}.` : "";
 40→  if (stage === prodStage) return `${prefix}${PRODUCTION}`;
 41→  if (stage === devStage) return `${prefix}${DEV}`;
 42→  return `${prefix}${stage}.${DEV}`;
 43→};
 44→
 45→export const domain = getDomain();
 46→
 47→// DB
 48→const database = await Database("Database", {
 49→  name: "laxdb",
 50→  clusterSize: "PS_5",
 51→  kind: "postgresql",
 52→  organization: "johnwatt",
 53→  allowDataBranching: true,
 54→  adopt: true,
 55→});
 56→
 57→// Dev/staging branch
 58→const devBranch = await Branch("dev-branch", {
 59→  name: "development",
 60→  organization: "johnwatt",
 61→  database,
 62→  parentBranch: "main",
 63→  isProduction: false,
 64→  adopt: true,
 65→});
 66→
 67→// Personal dev branch
 68→const personalBranch = await Branch("personal-branch", {
 69→  name: "personal",
 70→  organization: "johnwatt",
 71→  database,
 72→  parentBranch: "development",
 73→  isProduction: false,
 74→  adopt: true,
 75→});
 76→
 77→// Branch selection based on stage
 78→const currentBranch =
 79→  stage === prodStage
 80→    ? database.defaultBranch
 81→    : stage === devStage || stage.startsWith("pr-")
 82→      ? devBranch
 83→      : personalBranch;
 84→
 85→// Admin role for current branch
 86→const dbRole = await Role(`db-role-${stage}-v2`, {
 87→  database,
 88→  branch: currentBranch,
 89→  inheritedRoles: ["postgres"],
 90→});
 91→
 92→// Hyperdrive connection pooling
 93→const db = await Hyperdrive("hyperdrive", {
 94→  origin: dbRole.connectionUrl,
 95→  adopt: true,
 96→});
 97→
 98→// Generate Drizzle migrations
 99→await Exec("DrizzleGenerate", {
100→  command: "cd packages/core && bun run db:generate",
101→  env: {
102→    DATABASE_URL: dbRole.connectionUrl,
103→  },
104→  memoize: {
105→    patterns: ["drizzle.config.ts", "src/schema.ts"],
106→  },
107→});
108→
109→// Apply migrations to the database
110→await Exec("DrizzleMigrate", {
111→  command:
112→    process.platform === "win32"
113→      ? `cmd /C "cd packages/core && bun run db:migrate || if %ERRORLEVEL%==9 exit 0 else exit %ERRORLEVEL%"`
114→      : `sh -c 'cd packages/core && bun run db:migrate || ( [ $? -eq 9 ] && exit 0 ); exit $?'`,
115→  env: {
116→    DATABASE_URL: dbRole.connectionUrl,
117→  },
118→  memoize: {
119→    patterns: ["drizzle.config.ts", "drizzle/*.sql"],
120→  },
121→});
122→
123→// Start Drizzle Studio in local development
124→if (app.local) {
125→  Exec("DrizzleStudio", {
126→    command: "cd packages/core && bun run db:studio",
127→    env: {
128→      DATABASE_URL: dbRole.connectionUrl,
129→    },
130→  });
131→
132→  Exec("Storybook", {
133→    command: "cd packages/ui && bun run storybook",
134→  });
135→}
