  1→import { createFileRoute, Link, useNavigate } from "@tanstack/react-router";
  2→import { publishedPosts } from "@/lib/posts";
  3→
  4→import { formatPublishedDate } from "@/lib/date";
  5→import { getContentByTag, getContentByTags } from "@/lib/graph-utils";
  6→
  7→const FILTERS = [
  8→  { key: "all", label: "All" },
  9→  { key: "guide", label: "Guides" },
 10→  { key: "opinion", label: "Opinion" },
 11→  { key: "wiki", label: "Wiki" },
 12→] as const;
 13→
 14→type FilterKey = (typeof FILTERS)[number]["key"];
 15→
 16→export const Route = createFileRoute("/blog/")({
 17→  validateSearch: (search: Record<string, unknown>) => ({
 18→    filter: (FILTERS.some((f) => f.key === search.filter) ? search.filter : undefined) as
 19→      | FilterKey
 20→      | undefined,
 21→  }),
 22→  component: BlogIndex,
 23→});
 24→
 25→function getFilteredPosts(filter: FilterKey) {
 26→  switch (filter) {
 27→    case "guide":
 28→      return getContentByTags(publishedPosts, ["blog", "guide"]);
 29→    case "opinion":
 30→      return getContentByTags(publishedPosts, ["blog", "opinion"]);
 31→    case "wiki":
 32→      return getContentByTags(publishedPosts, ["blog", "wiki"], ["opinion"]);
 33→    default:
 34→      return getContentByTag(publishedPosts, "blog");
 35→  }
 36→}
 37→
 38→function BlogIndex() {
 39→  const { filter: searchFilter } = Route.useSearch();
 40→  const filter = searchFilter ?? "all";
 41→  const navigate = useNavigate();
 42→
 43→  const filteredPosts = getFilteredPosts(filter);
 44→  const sortedPosts = [...filteredPosts].toSorted(
 45→    (a, b) => new Date(b.published).getTime() - new Date(a.published).getTime(),
 46→  );
 47→
 48→  return (
 49→    <main className="mx-auto max-w-screen-sm px-4 py-16 md:py-32">
 50→      <header className="mb-10">
 51→        <h1 className="font-serif text-2xl text-foreground italic">Blog</h1>
 52→        <nav className="mt-5 flex items-baseline gap-5 border-b border-border">
 53→          {FILTERS.map((f) => {
 54→            const active = filter === f.key;
 55→            return (
 56→              <button
 57→                key={f.key}
 58→                type="button"
 59→                onClick={() =>
 60→                  navigate({
 61→                    to: "/blog",
 62→                    search: { filter: f.key === "all" ? undefined : f.key },
 63→                  })
 64→                }
 65→                className={`relative pb-2.5 text-sm tracking-wide transition-colors ${
 66→                  active ? "text-foreground" : "text-subtle hover:text-muted-foreground"
 67→                }`}
 68→              >
 69→                {f.label}
 70→                {active && <span className="absolute inset-x-0 -bottom-px h-px bg-foreground" />}
 71→              </button>
 72→            );
 73→          })}
 74→        </nav>
 75→      </header>
 76→      {sortedPosts.length === 0 ? (
 77→        <p className="text-muted-foreground">No posts yet.</p>
 78→      ) : (
 79→        <ul className="space-y-6">
 80→          {sortedPosts.map((post) => (
 81→            <li key={post.slug}>
 82→              <Link to="/content/$slug" params={{ slug: post.slug }} className="group block">
 83→                <article className="border-b border-border pb-6 transition-colors">
 84→                  <h2 className="font-serif text-lg text-foreground italic group-hover:text-muted-foreground">
 85→                    {post.title}
 86→                  </h2>
 87→                  {post.excerpt && (
 88→                    <p className="mt-2 text-sm text-muted-foreground">{post.excerpt}</p>
 89→                  )}
 90→                  <div className="mt-2 flex items-center gap-2">
 91→                    <span className="text-xs text-subtle">
 92→                      {formatPublishedDate(post.published)}
 93→                    </span>
 94→                    {post.tags && post.tags.some((t) => t !== "blog") && (
 95→                      <span className="text-xs text-subtle">
 96→                        · {post.tags.filter((t) => t !== "blog").join(", ")}
 97→                      </span>
 98→                    )}
 99→                  </div>
100→                </article>
101→              </Link>
102→            </li>
103→          ))}
104→        </ul>
105→      )}
106→    </main>
107→  );
108→}
