 1→import { createFileRoute, Link, notFound } from "@tanstack/react-router";
 2→import type { Post } from "content-collections";
 3→
 4→import { publishedPosts } from "@/lib/posts";
 5→
 6→import { formatPublishedDate } from "@/lib/date";
 7→import { MDXContent } from "@/components/mdx-content";
 8→
 9→export const Route = createFileRoute("/content/$slug")({
10→  loader: ({ params }: { params: { slug: string } }): Post => {
11→    const post = publishedPosts.find((p) => p.slug === params.slug);
12→    if (!post) {
13→      throw notFound();
14→    }
15→    return post;
16→  },
17→  component: ContentPage,
18→});
19→
20→function ContentPage() {
21→  const post = Route.useLoaderData();
22→
23→  // Determine content type based on tags
24→  const isWiki = post.tags?.includes("wiki");
25→  const isBlog = post.tags?.includes("blog");
26→
27→  return (
28→    <main className="mx-auto max-w-screen-sm px-4 py-16 md:py-32">
29→      <article>
30→        <header className="mb-8">
31→          <h1 className="font-serif text-2xl text-foreground italic">{post.title}</h1>
32→          <div className="mt-4 flex items-center gap-4 text-sm text-muted-foreground">
33→            {post.authors && post.authors.length > 0 && <span>{post.authors.join(", ")}</span>}
34→            <span>{formatPublishedDate(post.published)}</span>
35→          </div>
36→          {post.tags && post.tags.length > 0 && (
37→            <div className="mt-3 flex flex-wrap gap-2">
38→              {post.tags.map((tag) => (
39→                <Link
40→                  key={tag}
41→                  to="/tag/$tagId"
42→                  params={{ tagId: tag }}
43→                  className="rounded-full bg-border/50 px-2 py-0.5 text-xs text-subtle transition-colors hover:bg-border hover:text-foreground"
44→                >
45→                  {tag}
46→                </Link>
47→              ))}
48→            </div>
49→          )}
50→        </header>
51→        <MDXContent code={post.mdx} className="prose-blog prose max-w-none" />
52→        <footer className="mt-12 border-t border-border pt-6">
53→          <nav className="flex gap-4 text-sm">
54→            {isBlog && (
55→              <Link
56→                to="/blog"
57→                search={{ filter: undefined }}
58→                className="text-muted-foreground hover:text-foreground"
59→              >
60→                ← All Blog Posts
61→              </Link>
62→            )}
63→            {isWiki && (
64→              <Link to="/wiki" className="text-muted-foreground hover:text-foreground">
65→                ← Wiki Index
66→              </Link>
67→            )}
68→            <Link to="/graph" className="text-muted-foreground hover:text-foreground">
69→              View Graph
70→            </Link>
71→          </nav>
72→        </footer>
73→      </article>
74→    </main>
75→  );
76→}
