 1→name: Deploy Application
 2→on:
 3→  push:
 4→    branches:
 5→      - main
 6→    paths-ignore:
 7→      - "references/**"
 8→      - "docs/**"
 9→      - "scratchpad/**"
10→      - "README.md"
11→      - "AGENTS.md"
12→      - "TODO.md"
13→  pull_request:
14→    types:
15→      - opened
16→      - reopened
17→      - synchronize
18→      - closed
19→    paths-ignore:
20→      - "*.md"
21→      - "docs/**"
22→concurrency:
23→  group: deploy-${{ github.ref }}
24→  cancel-in-progress: false
25→env:
26→  STAGE: ${{ github.event_name == 'pull_request' && format('pr-{0}',
27→    github.event.number) || (github.ref == 'refs/heads/main' && 'prod' ||
28→    github.ref_name) }}
29→jobs:
30→  deploy:
31→    if: ${{ github.event.action != 'closed' }}
32→    runs-on: ubuntu-latest
33→    environment: ${{ github.ref == 'refs/heads/main' && 'prod' || 'staging' }}
34→    permissions:
35→      contents: read
36→      pull-requests: write
37→    steps:
38→      - uses: actions/checkout@v4
39→      - name: Setup Bun
40→        uses: oven-sh/setup-bun@v2
41→      - name: Install dependencies
42→        run: bun install --frozen-lockfile
43→      - name: Deploy
44→        run: bun alchemy deploy --stage ${{ env.STAGE }}
45→        env:
46→          ALCHEMY_PASSWORD: ${{ secrets.ALCHEMY_PASSWORD }}
47→          ALCHEMY_STATE_TOKEN: ${{ secrets.ALCHEMY_STATE_TOKEN }}
48→          CLOUDFLARE_API_TOKEN: ${{ secrets.CLOUDFLARE_API_TOKEN }}
49→          CLOUDFLARE_ACCOUNT_ID: ${{ secrets.CLOUDFLARE_ACCOUNT_ID }}
50→          CLOUDFLARE_EMAIL: ${{ secrets.CLOUDFLARE_EMAIL }}
51→          GOOGLE_CLIENT_ID: ${{ secrets.GOOGLE_CLIENT_ID }}
52→          GOOGLE_CLIENT_SECRET: ${{ secrets.GOOGLE_CLIENT_SECRET }}
53→          BETTER_AUTH_SECRET: ${{ secrets.BETTER_AUTH_SECRET }}
54→          POLAR_WEBHOOK_SECRET: ${{ secrets.POLAR_WEBHOOK_SECRET }}
55→          PLANETSCALE_ORGANIZATION: ${{ secrets.PLANETSCALE_ORGANIZATION }}
56→          PLANETSCALE_SERVICE_TOKEN: ${{ secrets.PLANETSCALE_SERVICE_TOKEN }}
57→          PLANETSCALE_SERVICE_TOKEN_ID: ${{ secrets.PLANETSCALE_SERVICE_TOKEN_ID }}
58→          PULL_REQUEST: ${{ github.event.number }}
59→          GITHUB_SHA: ${{ github.sha }}
60→          GITHUB_TOKEN: ${{ secrets.GITHUB_TOKEN }}
61→  cleanup:
62→    runs-on: ubuntu-latest
63→    if: ${{ github.event_name == 'pull_request' && github.event.action == 'closed' }}
64→    environment: staging
65→    permissions:
66→      id-token: write
67→      contents: read
68→      pull-requests: write
69→    steps:
70→      - uses: actions/checkout@v4
71→      - name: Setup Bun
72→        uses: oven-sh/setup-bun@v2
73→      - name: Install dependencies
74→        run: bun install --frozen-lockfile
75→      - name: Safety Check
76→        run: |-
77→          if [ "${{ env.STAGE }}" = "prod" ]; then
78→            echo "ERROR: Cannot destroy prod environment in cleanup job"
79→            exit 1
80→          fi
81→      - name: Destroy Preview Environment
82→        run: bun alchemy destroy --stage ${{ env.STAGE }}
83→        env:
84→          ALCHEMY_PASSWORD: ${{ secrets.ALCHEMY_PASSWORD }}
85→          ALCHEMY_STATE_TOKEN: ${{ secrets.ALCHEMY_STATE_TOKEN }}
86→          CLOUDFLARE_API_TOKEN: ${{ secrets.CLOUDFLARE_API_TOKEN }}
87→          CLOUDFLARE_ACCOUNT_ID: ${{ secrets.CLOUDFLARE_ACCOUNT_ID }}
88→          CLOUDFLARE_EMAIL: ${{ secrets.CLOUDFLARE_EMAIL }}
89→          GOOGLE_CLIENT_ID: ${{ secrets.GOOGLE_CLIENT_ID }}
90→          GOOGLE_CLIENT_SECRET: ${{ secrets.GOOGLE_CLIENT_SECRET }}
91→          BETTER_AUTH_SECRET: ${{ secrets.BETTER_AUTH_SECRET }}
92→          POLAR_WEBHOOK_SECRET: ${{ secrets.POLAR_WEBHOOK_SECRET }}
93→          PLANETSCALE_ORGANIZATION: ${{ secrets.PLANETSCALE_ORGANIZATION }}
94→          PLANETSCALE_SERVICE_TOKEN: ${{ secrets.PLANETSCALE_SERVICE_TOKEN }}
95→          PLANETSCALE_SERVICE_TOKEN_ID: ${{ secrets.PLANETSCALE_SERVICE_TOKEN_ID }}
96→          PULL_REQUEST: ${{ github.event.number }}
